    private void cbRoomActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_cbRoomActionPerformed
        if (cbRoom.getSelectedItem() == "Standart Room"){
            lbHargaRoom.setText("200000");
        }
        
        if (cbRoom.getSelectedItem() == "Superior Room"){
            lbHargaRoom.setText("300000");
        }
        
        if (cbRoom.getSelectedItem() == "Deluxe Room"){
            lbHargaRoom.setText("500000");
        }
    }//GEN-LAST:event_cbRoomActionPerformed

