    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
      this.toBack();
      setVisible(false);
      new HalUt().toFront();
      new HalUt().setState(java.awt.Frame.NORMAL);  
        
    }//GEN-LAST:event_jButton1ActionPerformed

    private void btnHargaActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnHargaActionPerformed
        int hargaRoom, tflamaHari, hargatotal;

        hargaRoom = Integer.parseInt(lbHargaRoom.getText());
        tflamaHari = Integer.parseInt(lamaHari.getText());

        if(jCheckBox1.isSelected()){
            hargatotal = (hargaRoom * tflamaHari) + (tflamaHari * 95000);
            lbHarga.setText(String.valueOf(hargatotal));
        }else{
            hargatotal = (hargaRoom * tflamaHari);
            lbHarga.setText(String.valueOf(hargatotal));
        }
    }//GEN-LAST:event_btnHargaActionPerformed

