    private void CbMakananActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_CbMakananActionPerformed
        if (CbMakanan.getSelectedItem()=="Bakso"){
            LbHargaMak.setText("10000");
            
        }
        
        if (CbMakanan.getSelectedItem()=="Soto"){
            LbHargaMak.setText("15000");
        }
        
        if (CbMakanan.getSelectedItem()=="Mie"){
            LbHargaMak.setText("4000");
        }
        
        if (CbMakanan.getSelectedItem()=="Capcay"){
            LbHargaMak.setText("6000");
        }
        
        if (CbMakanan.getSelectedItem() == ""){
            LbHargaMak.setText("0");
        }
    }//GEN-LAST:event_CbMakananActionPerformed

