    private void BtnHitungActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_BtnHitungActionPerformed
        int hargamak, hargamin, jummakanan, jumminuman, totalbelanja;
        
        hargamak = Integer.parseInt(LbHargaMak.getText());
        hargamak = Integer.parseInt(LbHargaMak1.getText());
        hargamak = Integer.parseInt(LbHargaMak2.getText());
        hargamak = Integer.parseInt(LbHargaMak3.getText());
        hargamak = Integer.parseInt(LbHargaMak4.getText());
        hargamin = Integer.parseInt(LbHargaMin.getText());
        hargamin = Integer.parseInt(LbHargaMin1.getText());
        hargamin = Integer.parseInt(LbHargaMin2.getText());
        hargamin = Integer.parseInt(LbHargaMin3.getText());
        hargamin = Integer.parseInt(LbHargaMin4.getText());
        jummakanan = Integer.parseInt(TfJumlahMak.getText());
        jumminuman = Integer.parseInt(TfJumlahMin.getText());
        totalbelanja=(hargamak*jummakanan) + (hargamin*jumminuman);
        LbHasil.setText(String.valueOf(totalbelanja));
    }//GEN-LAST:event_BtnHitungActionPerformed

